投稿用フォーム<form action ="20170820_kadai2_2-1.php" method ="post">	Name: <input type ="text" name ="name">	Comment: <input type ="text" name ="message">	<button type ="submit" name="action" value="send">GO</button></form>削除用フォーム<form action ="20170821_kadai2_4.php" method ="post">削除対象番号: <input type ="text" name ="number"><button type ="submit" name="action" value="delete">DELETE</button>        <?php	$num = 1;	$array[] = $num."＜＞";	$array[] = htmlspecialchars($_POST["name"])."＜＞";	$array[] = htmlspecialchars($_POST["message"])."＜＞";	$array[] = date("Y/m/d H:i");if($_SERVER["REQUEST_METHOD"]=="POST"){			//ファイルの中身を一行ずつ配列として取得		$line = file("kadai_2-2.txt");		//最終行の配列を取得		$finalline = $line[count($line)-1];		//最終行の配列を番号、名前、コメント、時刻の4つの配列に分断		$endline = explode("＜＞",$finalline);		//番号の配列に+1する		$endline[0] += 1;		//配列arrayに最終投稿番号+1の値を代入		$array[0] = $endline[0]."＜＞";		//kadai_2-2に投稿内容を追加保存していく		$fp = fopen("kadai_2-2.txt","a");		//for文で配列arrayの要素をすべて書き込む		for($i=0; $i<count($array); $i++){			fwrite($fp,$array[$i]);		}		////全て書き込んだ後は改行し、ファイルを閉じる		fwrite($fp,"\n");		fclose($fp);}		$contents=file("kadai_2-2.txt");foreach($contents as $line){$message=explode("＜＞",$line);echo '<p>',$message[0],'</p>';echo '<p>',$message[1],'</p>';echo '<p>',$message[2],'</p>';echo '<p>',$message[3],'</p>';echo '<p>',$message[4],'</p>';}?>